import React from 'react';
import internal from "stream";
// https://www.hltv.org/matches/2352820/natus-vincere-vs-vitality-blast-premier-fall-final-2021
// https://chewedup.blast.tv/6544bb744b36dbf16919bd9b88528887b06a71c7de9e60e5102bba7c1cb9611c.html

interface MatchLine {
    isPlayer: boolean;
    isWarmup: boolean;
    steamId: string;
    team: string;
    type: string;
    roundNumber: number;
    raw: string;
}

interface Player {
    steamId: string;
}

interface State {
    loading: boolean;
    players: Player[];
    matchLines: MatchLine[];
}
// interface MyState {
//     matchLines: keyof MatchLine;
// }

class App extends React.Component <any, State> {
// class App extends React.Component {
//     constructor(props: any) {
