    //     // const groupedRounds = this.state.matchLines.reduce((tmpGroupedRounds, { roundNumber, line }) => {
    //     //     if (!tmpGroupedRounds[roundNumber]) {
    //     //         tmpGroupedRounds[roundNumber] = [];
    //     //     }
    //     //     tmpGroupedRounds[roundNumber].push(line);
    //     //     return tmpGroupedRounds;
    //     // }, {});
    //     // console.log(groupedRounds);
    //
