import React from 'react';
// https://www.hltv.org/matches/2352820/natus-vincere-vs-vitality-blast-premier-fall-final-2021
// https://chewedup.blast.tv/6544bb744b36dbf16919bd9b88528887b06a71c7de9e60e5102bba7c1cb9611c.html

interface MatchLine {
    isPlayer: boolean;
    isWarmup: boolean;
    steamId: string;
    team: string;
    type: string;
    interactionData: InteractionData;
    roundNumber: number;
    raw: string;
}

interface InteractionData {
    damage: number;
    kill: boolean;
    assist: boolean;
}

interface Player {
    steamId: string;
    alias: string;
}

interface Round {
    number: number;
    // stats?: RoundStats;
    // playerStats?: {};
}

interface PlayerStats {
    player: Player;
    kills: number;
    deaths: number;
    assists: number;
    damage: number;
}

interface RoundStats {
    score: {},
    playerStats?: any;
}

interface State {
    loading: boolean;
    players: Player[];
    rounds: Round[];
    roundStats: RoundStats[];
    matchLines: MatchLine[];
}
