    renderStats = () => {
        console.log(this.state);
        this.setState({'loading': false});
    }

    calculateStats = () => {
        console.log(this.state);

        const roundStatsCollection : RoundStats[] = [];
        const playerStats = this.getDefaultPlayerStats();
        const score: {[key: string]: number} = {
            ct: 0,
            t: 0
        }

