import React from 'react';
import internal from "stream";
// https://www.hltv.org/matches/2352820/natus-vincere-vs-vitality-blast-premier-fall-final-2021
// https://chewedup.blast.tv/6544bb744b36dbf16919bd9b88528887b06a71c7de9e60e5102bba7c1cb9611c.html

interface MatchLine {
    isPlayer: boolean;
    isWarmup: boolean;
    steamId: string;
    team: string;
    type: string;
    roundNumber: number;
    raw: string;
}

interface Player {
    steamId: string;
}

// interface MyState {
//     matchLines: keyof MatchLine;
// }

class App extends React.Component {
// class App extends React.Component {
//     constructor(props: any) {
//         super(props);
//
//         this.state = {
//             loading: true,
//             matchLines: ''
//         };
//     }
    state = {
        // loading: false,
        matchLines: [],
        players: [],
        rounds: [],
        score: [],
    };

    // loading = (loading: boolean) => {
    //     this.setState({'loading': loading});
    //     console.log(this.state);
    // }



    calculateStats = () => {
        console.log(this.state.players);
    }

    processMatchLines = (data: string) => {
        //this.setState({'matchLines': data});

        var lines = data.split('\n');

        // Find last Match_Start.
        var reversedLines = data.split('\n').reverse().join('\n');
        var matchStartIndex = reversedLines.indexOf('Match_Start'); // => 18
        var tempString = reversedLines.substring(0, matchStartIndex);
        var MatchStartLineNumber = tempString.split('\n').length;

        // Loop through.
        const matchLines : MatchLine[] = [];
        let roundNumber = 0;

        for (var line = 0; line < lines.length; line++) {
            let currentLine = lines[line].substring(24);
            let steamId = this.determineMatchLineSteamId(currentLine);
            let isPlayer = steamId !== '';
            let isWarmup = line < MatchStartLineNumber;
            let type = this.determineMatchLineType(currentLine);;
            let team = this.determineMatchLineTeam(currentLine);

            if (type == 'round_end') {
                roundNumber++;
            }

            let lineObject: MatchLine = {
                'isPlayer': isPlayer,
                'isWarmup': isWarmup,
                'steamId': steamId,
                'team': team,
                'type': type,
                'roundNumber': isWarmup ? 0 : roundNumber,
                'raw': lines[line],
            }

            matchLines.push(lineObject);
        }

        this.setState({ 'matchLines': matchLines});
        console.log('process done');
    }

    determineMatchLineSteamId = (line: string) => {
        let steamId = line.match('STEAM_[^>]*');

        if (steamId) {
            const isKnownPlayer = this.state.players.find((player: any) => {
                console.log(player);
                return player.steamId === steamId;
            });

            if (!isKnownPlayer) {
                const players: Player[] = this.state.players.slice();

                let newPlayer: Player = {
                    'steamId': steamId[0]
                };

                players.push(newPlayer);

                this.setState({
                    players: players
                })
            }

            return steamId[0];
        }

        return '';
    }

    determineMatchLineTeam = (line: string) => {
        if (line.match(/<CT>|<TERRORIST>/)) {
            return line.includes('<CT>') ? 'CT' : 'T';
        }

        return '';
    }

    determineMatchLineType = (line: string) => {
        if (line.match(/killed|attacked/)) {
            return 'damage';
        }
        if (line.match(/Round_End/)) {
            return 'round_end';
        }
        if (line.match(/Round_Start/)) {
            return 'round_start';
        }
        if (line.match(/SFUI_Notice_Terrorists_Win/)) {
            return 'round_win_t';
        }
        if (line.match(/SFUI_Notice_CTs_Win/)) {
            return 'round_win_ct';
        }

        return 'meta';
    }

    processMatch() {
        console.log('renderMatch');
        const file = require("./matchdata/match.txt");

        fetch(file)
            .then((response) => response.text())
            .then((data) => {
                this.processMatchLines(data);
            })
            .then(() => {
                this.calculateStats();
            });
    }

    componentDidMount() {
        this.processMatch();
    }

    render() {
        return (
            <div>kk</div>
        );
    }
}

export default App;
