import React from 'react';

interface MatchLine {
    isPlayer: boolean;
    isWarmup: boolean;
    team: string;
    type: string;
    raw: string;
}

// interface MyState {
//     matchLines: keyof MatchLine;
// }

class App extends React.Component {
// class App extends React.Component {
