    state = {
        loading: false,
        matchLines: '',
    };

    setStateData = (key: string, value: any) => {
        this.setState({key: value});
        console.log(this.state);
    }

    k() {
        if (this.state.loading == true) {
            return;
        }

        this.setState({ loading: true });

        const file = require("./matchdata/match.txt");

        fetch(file)
            .then((response) => response.text())
            .then((data) => {
                console.log(data);
                this.setStateData('matchLines', data );
                console.log(this.state.matchLines);
            })
            .then(() => {
                console.log('txt done');
                this.setStateData('loading', false);
            });
    }
    componentDidMount() {
        this.k();
    }

    render() {
        console.log('render');
        var matchLines = this.state.loading == true ? 'loading' : this.state.matchLines;

        console.log(this.state.matchLines);

        return (
            <ul>
              { matchLines }
            </ul>
        );
    }
}

export default App;
